import icon from "../assets/logo.png"
import "./searchPage.css"
import axios from "axios";
import {CloseCircleOutlined, SearchOutlined} from "@ant-design/icons";
import {useState} from "react";



const SearchPage = () => {
    const [hasFocus, setHasFocus] = useState(false)
    const [query, setQuery] = useState('')
    const [searchResult, setSearchResult] = useState([])

    const handleChange = (event) => {
        setQuery(event.target.value);
    }


    const search = (event) => {
        if (event.key === 'Enter') {
            const url = `http://192.168.0.106:8081/search?keywords=${query}`
            axios.get(url)
                .then((response) => {
                    setSearchResult(response.data)
                })
                .catch((error) => {
                    console.log('error', error.response.data.message, error.message)
                })
        }
    }

    const listResult = Array.isArray(searchResult) && setSearchResult.length > 0?searchResult.map((value, key) => {
        return <div key={key}>
            <div>title: {value.title}</div>
            <div>score: {value.score}</div>
            <a href={value.url}>{value.url}</a>
            <div>last modified date: {value.lastModifiedDate}</div>
            <div>size: {value.size}</div>
        </div>
    }):() => {
        return <div>no result</div>
    }

    return (
        <div className={"main-body"}>
            <div className={"search-body"}>
                <div className={"logo"}>
                    <img src={icon} alt=""/>
                </div>
                <div className={"searchbar"}>
                    <SearchOutlined className={"mg"}/>
                    <input
                        value={query}
                        onChange={handleChange}
                        onFocus={() => {setHasFocus(true)}}
                        onBlur={() => {setHasFocus(false)}}
                        onKeyUp={(event) => search(event)}
                        id={"search"} />
                    {hasFocus && !!query && (
                        <div className='my-input-clear'
                             onMouseDown={(e) => {
                                 e.preventDefault()
                             }}
                             onClick={() => {
                                 setQuery('')
                             }}>
                            <CloseCircleOutlined />
                        </div>
                    )}

                </div>
            </div>
            <div className={"searchResult-body"}>
                <div>
                    {listResult}
                </div>
            </div>

        </div>
    );
}
export default SearchPage;
