import icon from "../assets/logo.png"
import "./searchPage.css"
import axios from "axios";
import {CloseCircleOutlined, SearchOutlined} from "@ant-design/icons";
import {useState} from "react";



const SearchPage = () => {
    const [hasFocus, setHasFocus] = useState(false)
    const [query, setQuery] = useState('')
    const [hasSearch, setHasSearch] = useState(false)
    const [searchResult, setSearchResult] = useState([])

    const handleChange = (event) => {
        setQuery(event.target.value);
    }


    const search = (event) => {
        if (event.key === 'Enter') {
            setHasSearch(true)
            const url = `http://10.79.168.241:8081/search?keywords=${query}`
            axios.get(url)
                .then((response) => {
                    setSearchResult(response.data)
                })
                .catch((error) => {
                    console.log('error', error.response.data.message, error.message)
                })
        }
    }

    const listResult = Array.isArray(searchResult) && searchResult.length > 0?searchResult.map((value, key) => {
        return <div className={"result-item"} key={key}>
            <a href={value.url}>{value.title}</a>
            <div>score: {value.score}</div>
            <a href={value.url}>{value.url}</a>
            <div>last modified date: {value.lastModifiedDate}</div>
            <div>size: {value.size}</div>
            <div>{value.freqList.map((value, key) => {
                return <span>({value.keyword}, {value.frequency}) </span>
            })}</div>
            <div>parent link: {value.parentLinks.map((value, key) => {
                return <div><a href={value}>{value}</a></div>
            })}</div>
        </div>
    }):hasSearch?<div>no result</div>:null

    return (
        <div className={"main-body"}>
            <div className={"search-body"}>
                <div className={"logo"}>
                    <img src={icon} alt=""/>
                </div>
                <div className={"searchbar"}>
                    <SearchOutlined className={"mg"}/>
                    <input
                        value={query}
                        onChange={handleChange}
                        onFocus={() => {setHasFocus(true)}}
                        onBlur={() => {setHasFocus(false)}}
                        onKeyUp={(event) => search(event)}
                        id={"search"} />
                    {hasFocus && !!query && (
                        <div className='my-input-clear'
                             onMouseDown={(e) => {
                                 e.preventDefault()
                             }}
                             onClick={() => {
                                 setQuery('')
                             }}>
                            <CloseCircleOutlined />
                        </div>
                    )}

                </div>
            </div>
            <div className={"result-body"}>
                <div className={"result-list"}>
                    {listResult}
                </div>
            </div>

        </div>
    );
}
export default SearchPage;
